<script>

export default {
	name: 'home',
	data() {
		return {
			temperature: '',
			wind: '',
			description: '',
			day: '',
		}
	},
	created() {
		this.fetchNew();
		this.getWaves();
	},

	methods: {

		async fetchNew() {
			const url = 'https://goweather.herokuapp.com/weather/hoddevik';
			const res = await fetch(url);
			const { forecast }   = await res.json();

			this.temperature = forecast[0].temperature
			this.wind = forecast[0].wind
			this.day = forecast[0].day
			
		},
		async getWaves() {
			const url = 'https://api.stormglass.io/v2/weather/point?lat=45.440845&lng=12.315515&params=';
			const options = {
				headers: {
