			description: '',
			cloudy: false,
			sunny: false,
			rain: false
		}
	},
	created() {
		// this.fetchNew();
		this.today();
		this.getWaves();
		this.getDate();
	},

	methods: {

		async fetchNew() {
			const url = 'https://goweather.herokuapp.com/weather/oslo';
			const res = await fetch(url);
			const result   = await res.json();

			this.temperature = result.forecast[this.index].temperature
			this.wind = result.forecast[this.index].wind

			// setter verdiene fra APIet til temp og vind
			
		},

		getDate() {
			this.date = new Date().toString();
			this.date = this.date.slice(4, 10)
			this.day = new Date().toString();
			this.day = this.day.slice(0, 4)
		},

		async today() {
			const url = 'https://goweather.herokuapp.com/weather/oslo';
			const res = await fetch(url);
			const result   = await res.json();

			this.temperature = result.temperature
			this.wind = result.wind
			this.togle = false
			this.day = 'today'
			this.description = result.description
			// henter verdier og setter de til rett plass
			
			if (this.description === 'Partly cloudy') {
				console.log(this.description)
				this.cloudy = true
				this.sunny = false
				this.rain = false
			} 
			else if (this.description === 'Heavy Rain' || this.description === 'Light Rain' || this.description === 'Showers' || this.description === 'Light rain shower') {
				this.cloudy = false
			 	this.sunny = false
				this.rain = true
			}

			else {
				this.cloudy = false
				this.sunny = true
				this.rain = false
			}

			// leser verdiene fra APIet og setter riktig VSG til riktig "description"
		},

		tomorrow() {
			this.index = 0
			this.fetchNew()
			this.togle = false
			this.day = 'tomorrow'	
			// henter data fra morgendagen
		},

		dayAfter() {
			this.index += 1
			this.fetchNew()
			this.togle = false
			this.day = 'day after tomorrow'
			// henter data fra over imorgen
		},


	}
}
</script>

